    private void btnGuardarLibActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarLibActionPerformed
        // Guardar info dentro de txt en form de agregar libro
        String Titulo = txtTitulo.getText();
        String Autor = txtAutor.getText();
        Integer NPaginas = Integer.parseInt(txtNpag.getText());
        String Editorial = txtEditorial.getText();
        String ISBN = txtISBN.getText();
        String FechaDePublicacion = txtAno.getText();
        String UnidadesDisponibles = txtUnd.getText();
        //enviar info a la logica en el metodo guardar libro
        Logica.GuardarLibro(Titulo,Autor,NPaginas,Editorial,ISBN,
                FechaDePublicacion,UnidadesDisponibles);
        //mensaje de guardado con exito
        JOptionPane.showMessageDialog(null,"Ha sido guardado con exÃ­to");
        
        // limpiar todo
        txtAutor.setText("");
        txtAno.setText("");
        txtEditorial.setText("");
        txtISBN.setText("");
        txtNpag.setText("");
        txtTitulo.setText("");
        txtUnd.setText("");
    }//GEN-LAST:event_btnGuardarLibActionPerformed

